css:
@media print{@page {size:A4}}

@media print {
    .page-break {page-break-after: always;}
}
.body {
  padding-bottom: 120px;
}
.footer{
   position: fixed;
   left: 0;
   bottom: 0;
   width: 100%;
   height: 120px;
}

html:
{% set print_num = frappe.db.get_single_value('Sales Invoice Print Settings', 'lab_print_serial') %}
{% set print_serial_khtab = frappe.db.get_value('Item', {"item_name":"خطاب المعمل المركزي"}, "custom_khtab_lab_print_serial") %}
{% set khetab_num = doc.items[0].custom_khetab_print_serial %}

{% set name = doc.name %}
{% set result = name.split('-') %}
{% set third = result[2] %}


{%- if doc.custom_service_group == 'إعادة طباعة خطاب المعمل المركزي' -%}
{% set doc_list = frappe.get_list('Sales Invoice', filters={'custom_service_group': 'خطاب المعمل المركزي', 'customer_name' : doc.customer_name, 'status':'Paid'}, order_by='creation desc', limit=1) %}
{%- if doc_list -%}
{% set last_doc = doc_list[0] %}
{% set george = last_doc.name %}
{% set third = george.split('-')[2] %}
{%- endif -%}
{%- endif -%}

{% set barcode = khetab_num ~ " " ~ third %}

{%- if doc.custom_last_print_serial_for_reprint_khetab > 0 -%}
{% set barcode = doc.custom_last_print_serial_for_reprint_khetab ~ " " ~ third %}
{% set khetab_num = doc.custom_last_print_serial_for_reprint_khetab %}
{%- endif -%}

{% set date = doc.posting_date|string %}
{% set current_year = date.split('-')|first %}

<div class="body">
{% if doc.custom_header_and_footer == 1 %}
<img src="/files/letter-head-AEC-Herder.png" alt="Header" width="100%"> 
{% endif %}
<table>
    <tr>
        <td style="width: 33%"><img width="80%" alt='Barcode Generator TEC-IT' src='https://barcode.tec-it.com/barcode.ashx?data={{barcode}}&amp;code=&amp;translate-esc=true'></td>
        <td  style="width: 40%; text-align: center"><b><u>رقم الخطاب:&ensp; {{khetab_num}}</u></b></td>
        <td style="width: 27%"></td>
    </tr>
</table>

{% set custom_registration_number_in_exporter_register = frappe.db.get_value('Customer', customer, 'custom_registration_number_in_exporter_register') %}

<h4 style="text-align: center;">السادة: المعمل المركزي لتحليل متبقيات المبيدات</h4>
<br>
<h4 style="text-align: right;"><b>تحية طيبه و بعد  ،،، </b></h4>
<br>
<div style="text-align: center; font-size: 18px;">يرجي التفضل بالعلم بأن شركة: {{doc.customer_name}}</div>

<table style="width: 100%; border-collapse: collapse; font-size: 18px;" border="0">
    <tr>
        <td style="text-align:right; width: 40%; font-size: 18px;">&ensp; رقم السجل المصدرين:</td>
        <td style="text-align:right; width: 60%; font-size: 18px;">&ensp; {{custom_registration_number_in_exporter_register}}</td>
    </tr>
</table>
<br>

{% set customer_data = frappe.get_doc("Customer", doc.customer) %}

<div style="text-align: center; font-size: 18px;">عضو بالمجلس التصديري للحاصلات الزراعية ومسجلة لدي المجلس بكود: {{customer_data.custom_company_code}}</div>
<br>
<div style="text-align: center; font-size: 18px;">مع العلم بأن الشركة مسددة للإشتراك السنوي للمجلس عن عام {{current_year}} باللجان الآتيه:</div>


{% set custom_committees_you_would_like_to_join = customer_data.custom_committees_you_would_like_to_join %}

<table style="height: 90px; width: 100%; border-collapse: collapse; font-size: 18px;" border="0">
    {%- for item in custom_committees_you_would_like_to_join -%}
    {% set committees = item.committees %}
    <tr>
        <td style="text-align: right; width: 30%; font-size: 18px;">&ensp; لجنه{{ loop.index }}:</td>
        <td style="text-align: right; width: 70%; font-size: 18px;">&ensp; {{ committees }}</td>
    </tr>
    {%- endfor -%}
</table>

<br>
<div style="text-align: center; font-size: 18px;">و هذا شهادة منا بذلك، دون أدني مسئولية علي المجلس.</div>
<br>
<div style="text-align: left; font-size: 18px;">و تفضلوا بقبول فائق الإحترام و التقدير،،،</div>

{% set full_name = frappe.db.get_value('Employee', {'designation': "المدير التنفيذي"}, 'employee_name') %}
{% set designation = frappe.db.get_value('Employee', {'designation': "المدير التنفيذي"}, 'designation') %}
<br>
<div style="text-align: left; font-size: 18px;">{{full_name}}</div>

<div style="text-align: left; font-size: 18px;">{{designation}}</div>
<div style="text-align: right; font-size: 18px;">{{doc.posting_date}}</div>
<div style="text-align: right; font-size: 18px;">{{doc.posting_time}}</div>
</div>


{% if doc.custom_header_and_footer == 1 %}
<div class="footer">
    <img src="/files/letter-head-AEC-Footer1.png" alt="Header" width="100%"> 
</div>
{% endif %}
